package coder.test.e_commerce.adapters

import android.content.Context
import android.content.Intent
import android.os.Bundle
import android.view.LayoutInflater
import android.view.View
import android.view.ViewGroup
import androidx.recyclerview.widget.RecyclerView
import coder.test.e_commerce.ProductActivity
import coder.test.e_commerce.R
import coder.test.e_commerce.models.Category
import com.squareup.picasso.Picasso
import kotlinx.android.synthetic.main.cat_row.view.*

class CategoryAdapter(val context: Context, val cats: List<Category>) :
    RecyclerView.Adapter<CategoryAdapter.ViewHolder>() {

    val catImages = arrayOf(
        "data:image/jpeg;base64,",
        "https://images-na.ssl-images-amazon.com/images/I/51gLWjcR8QL._SX425_.jpg",
        "https://i.dell.com/is/image/DellContent//content/dam/global-site-design/product_images/dell_client_products/desktops/inspiron_desktops/inspiron_22_3280_aio/pdp/desktop-inspiron-22-3280-aio-na-latam-pdp-gallery-504x350.jpg?fmt=jpg&wid=570&hei=400",
        "https://images.ctfassets.net/a9odgsv44wmq/3CQmCQiFu5DCWP3J7UTeSG/2d8bfb3947c2727a3d08709cb7726b8b/MenuImage_CocaCola.png?w=310&h=310",
        "https://d3njjcbhbojbot.cloudfront.net/api/utilities/v1/imageproxy/https://coursera-course-photos.s3.amazonaws.com/b0/aae5e0b59611e58ae18543e6165465/fruit-bowl.jpg?auto=format%2Ccompress&dpr=2.625",
        "https://target.scene7.com/is/image/Target/5xtmf-living-room-furniture-QUIVER-190408-1554761698055",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTzJWw7PfeKyUmnkXafoQWxL87kE64SwSfIORjnEWrDH2vRs8vcrQ&s",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcT86YnHafNvsZtybA51dFhLfFatQLam5CfYd8KmmQ_tw9xCvu6Z&s",
        "https://rukminim1.flixcart.com/image/714/857/shirt/f/c/g/bfshtpc501rd-being-fab-40-original-imaecvnx9swvsvp5.jpeg?q=50",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcTSs4w_a5ESm5Ss_kKo2p55LJrJAFkXitvRGwecpKvBHAjpPNVejg&s",
        "https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSNvRooteGayfZ7b9vZNk8ftaR_nYroE142dP5DsxE9BpUKvIw7&s",
        "data:image/jpeg;base64,",
        "data:image/jpeg;base64,"
    )

    override fun onCreateViewHolder(parent: ViewGroup, viewType: Int): ViewHolder {
        val v = LayoutInflater.from(context).inflate(R.layout.cat_row, parent, false)
        return ViewHolder(v)
    }

    override fun getItemCount(): Int = cats.size

    override fun onBindViewHolder(holder: ViewHolder, position: Int) {

        val cat = cats[position]
        holder.itemView.catTv.text = cat.name
//        Picasso.get().load(cat.image).into(holder.itemView.catImg)
        Picasso.get().load(catImages[position]).into(holder.itemView.catImg)

        holder.itemView.catImg.setOnClickListener {
            val intent = Intent(context,ProductActivity::class.java)
            val bundle = Bundle()
            bundle.putInt("cat_id",cat.id)
            intent.putExtras(bundle)
            context.startActivity(intent)
        }

    }

    class ViewHolder(item: View) : RecyclerView.ViewHolder(item)
}